import React from "react";
import {NavLink} from 'react-router-dom'
import styled from "styled-components";
import {tablet} from '../Responsive'

const Register = () => {
	const initialValue = {
		name: '',
		email: '',
		password: '',
		isMember: true
	}
	const handleChange = (e) => {}
		const handleSubmit = (e) => {}

	const Wrapper = styled.div`
	width: 100vw;
	height: 100vh;
	display: flex;
	justify-content: center;
	align-items: center;

	.main{
		background: #C6D0CE;
		width: 80%;
		height: 400px;
		display: flex;
	 justify-content: center;
	 flex-direction: column;
	 align-items: center;
	 ${tablet({width: '50%'})}
	}
	.heading, login{
		display: flex;
	 justify-content: center;
	 align-items: center;
	}
	img {
		width: 30px;
		width: 30px;
	}

	h3{
		font-weight: 600;
		font-size: 32px;
	}
	form{
		display: flex;
		flex-direction: column;
		width: 200px;
	}
	input{
		margin-bottom: 10px;
	}
	.btn{
		font-weight: bold;
		background: #528;
		color: white;
		padding: 5px;
	}
	`

	return (
	 <Wrapper>
	  <div className='main'>
	    <div className='heading'>
	     <img src='/images/logo.png' alt='brand' />
	     <h3>World jobs</h3>
	    </div>
	    <form onSubmit={handleSubmit}>
	      <label>Name</label>
	      <input type='text' name='name' />
	      <label>Email</label>
	      <input type='email' name='email' />
	      <label>Password</label>
	      <input type='password' name='password' />
	      <label>Name</label>
	      <input type='text' name='name' />
	      <button className='btn' type='submit'>Submit</button>
	    </form>
	    <div className='login'>
	     <p>Already a member?</p>
	     <NavLink to='/login'>Login</NavLink>
	    </div>
	  </div>




	 </Wrapper>
	)
}

export default Register
